test = 'mjqjpqmgbljsphdztnvjfqwrcgsmlb'
input = ''

def find_marker(s, l):
	return next(filter(lambda i: len(set(s[i:i + l])) == l, range(len(s) - l))) + l
	#for i in range(len(s) - 1):
	#	if len(set(s[i:i + l])) == l:
	#		return i + l
	#raise Exception('no marker found')
		
assert find_marker(test, 4) == 7
print('part 1:', find_marker(input, 4))

assert find_marker(test, 14) == 19
print('part 2:', find_marker(input, 14))
